import React, { Component } from 'react';
import styled from 'styled-components'
import tileImg from '../resources/tile.jpg'

const Main = styled.div`
    position: absolute;
    top: 50px;
    left: 50px;
    width: ${props => props.width};
    height: ${props => props.height};
    border: solid;
    border-width: 1px;
    box-shadow: 5px 5px 8px 4px rgba(0, 0, 0, 0.5);
`;

const Background = styled.div`
    float: left;
    line-height: 0px;
`;

class Board extends Component {
    state = {
        width: 0,
        height: 0,
        rowOfTile: this.props.row,
        columnOfTile: this.props.col
    }

    constructor(props) {
        super(props);

        const offsetW = 50;
        const offsetH = 50;
        this.setState({
            width: props.row * offsetW,
            height: props.col * offsetH,
            ...this.state
        });
    }

    componentWillMount() {
        this.GetObjects();
    }
    
    GetObjects = async () => {
        const objects = await this.CallApi();
        this.setState ({
            ...this.state
        })
    }

    CallApi = () => {
        return(
            console.log('call api')
        );
    }

    render() {
        const boardWidth = this.state.width + 'px';
        const boardHeight = this.state.height + 'px';
        const row = this.state.rowOfTile;
        const col = this.state.columnOfTile; 

        return (  
            <Main width={boardWidth} height={boardHeight}>
                <Tile row={row} col={col}/>
            </Main>      
        );
    }
}

class Tile extends Component {
    state = {
        offsetWidth: this.props.width,
        offsetHiehgt: this.props.height,
        row: this.props.row,
        col: this.props.col
    }

    CreateTile = () => {
        let body = [];
        const row = this.state.row;
        const col = this.state.col;

        for(let i = 0; i < row; ++i) {
            for(let j = 0; j < col; ++j) {
                body.push(<img src={tileImg} width={offsetWidth} height={offsetHiehgt} />);
            }
            body.push(<br />);
        }

        return body;
    }

    render() {
        return (
            <Background>
                {this.CreateTile()}
            </Background>
        );
    }
}

export default Board;
