import React, { Component } from 'react';
import styled from 'styled-components';
import Draggable from './Draggable';

const Content = styled.textarea`
    width: 200px;
    height: 200px;
    border: solid;
    border-width: 1px;
    box-shadow: 5px 5px 8px 4px rgba(0, 0, 0, 0.5);
    background-color: #BAECFF;
    font-size: 1.5em;
    resize: none;
    overflow: hidden;
    maxlength: 50;
`;

class PostIt extends Component {
    constructor(props) {
        super(props);

        this.items = null;
    }

    componentWillReceiveProps(nextProps) {
        this.items = nextProps.items;
    }

    /*componentDidUpdate() {
        const board = document.getElementsByClassName('board');
        const texts = board[0].getElementsByTagName('textarea');
        
        for(let i = 0; i < this.items.length; ++i) {
            let itemTexts = this.items[i].getElementsByTagName('textarea');

            texts[i].removeAttribute('readOnly');
            texts[i].addEventListener('change', this.OnChange);

            for(let itemText of itemTexts) {
                itemText = texts[i];
            }
        }
        
        console.log(this.items);
    }

    OnChange = (e) => {
        console.log(this.items)
