            }
        }

        this.props.setItems(this.items);
    }*/
    
    render() {
        return (
            <Draggable tag="board" addItem={this.props.addItem} customEvent={this.OnChange}>   
                <form>
                    <Content readOnly onChange={this.OnChange} />
                </form>
            </Draggable>
        );
    }
}

export default PostIt;
