import React, { Component } from 'react';
import styled from 'styled-components';

import Board from './components/Board';
import Symbol from './components/Symbol';
import PostIt from './components/PostIt';
import ImageLoader from './lib/ImageLoader';

const Symbols = styled.div`
  position: absolute;
  top: 50px;
  left: 1100px;
`;

const ButtonBox = styled.form`
  position: absolute;
  top: 850px;
  left: 950px;
`;

const Button = styled.button`
  border: 1px solid;
  padding: 5px;
  background-color: rgba(0, 0, 0, 0);
  
  &:hover {
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.5);
  }
`;

const PostBox = styled.div`
  position: absolute;
  top: 50px;
  left: 1300px;
`;

const Main = styled.div`
  user-select: none;
  marigin:auto;
`;

class App extends Component {
  constructor() {
    super();

    this.state = {
      items: [],
      prevItems: []
    };
  }

  addItem = (element) => {
    this.setState({
      items: [
        ...this.state.items,
        element
      ]
    })
  }

  setItems = (elements) => {
    this.setState({
      items: elements
    })
  }

  Back = (e) => {
    e.preventDefault();
    console.log('back');
  }

  Draw = (e) => {
    e.preventDefault();
    console.log('draw');
  }

  render() {
    let symbols = [];

    for(let key in ImageLoader) {
      symbols.push(<Symbol image={ImageLoader[key]} addItem={this.addItem} key={key} />)
    }

    let items = this.state.items;
    for(let i = 0; i < items.length; ++i){
      items[i].id = i; 
    }

    return (      
      <Main>
        <Board row={15} col={20} setItems={this.setItems} items={this.state.items} />
        <ButtonBox>
            <Button action={this.Back}>Back</Button>
            <Button action={this.Draw}>Draw</Button>
        </ButtonBox>
        <Symbols>
          {symbols}
        </Symbols>
        <PostBox>
          <PostIt setItems={this.setItems} addItem={this.addItem} items={this.state.items} />
        </PostBox>
      </Main>
    );
  }
}


export default App;
