import React, { Component } from 'react';
import styled from 'styled-components';

import Board from './components/Board';
import Symbol from './components/Symbol';
import PostIt from './components/PostIt';
import ImageLoader from './lib/ImageLoader';

const Symbols = styled.div`
  position: absolute;
  top: 50px;
  left: 1100px;
`;

const PostBox = styled.div`
  position: absolute;
  top: 50px;
  left: 1300px;
`;

const Main = styled.div`
  user-select: none;
`;

class App extends Component {
  constructor() {
    super();

    this.state = {
      items: []
    };
  }

  addItems = (element) => {
    this.setState({
      items: [
        ...this.state.items,
        element
      ]
    })
  }

  setItems = (element) => {
    console.log('setItems');
    console.log(element, this.state.items);
    
    this.setState({
      items: [
        element
      ]
    })
  }

  render() {
    let symbols = [];

    for(let key in ImageLoader) {
      symbols.push(<Symbol image={ImageLoader[key]} addItems={this.addItems} key={key} />)
    }

    return (      
      <Main>
        <Board row={15} col={20} setItems={this.setItems} items={this.state.items} />
        <Symbols>
          {symbols}
        </Symbols>
        <PostBox>
          <PostIt setItems={this.setItems} addItems={this.addItems} items={this.state.items} />
        </PostBox>
      </Main>
    );
  }
}


export default App;
