import React, { Component } from 'react';

function usesLeftButton(e) {
    const button = e.buttons || e.which || e.button;
    return button === 1;
}

class Draggable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            leftOffset: 0,
            topOffset: 0,
            left: 0,
            top: 0,
            clicked: false,
            dragging: false,
        }
        
        this.containerElem = null;
    }    

    componentDidMount() {
        const imgs = this.containerElem.getElementsByTagName('IMG');
        for (let i = 0; i < imgs.length; ++i) {
          imgs[i].setAttribute('draggable', 'false');
        }

        this.addListeners(this.containerElem);
        this.containerElem.style.cursor = 'grab';
    }

    addListeners = (element) => {
        element.addEventListener('mousedown', (e) => { this.handleMouseDown(e); }, false);
    };

    handleMouseDown = (e) => {
        if(usesLeftButton(e))
        {
            document.addEventListener('mousemove', this.handleMouseMove);
            document.addEventListener('mouseup', this.handleMouseUp);
            this.startDrag(e.clientX, e.clientY);
            this.containerElem.style.cursor = 'grabbing';
        }
    }

    startDrag = (clientX, clientY) => {
        console.log('startDrag')
        const rect = this.containerElem.getBoundingClientRect();
        this.setState({
            clicked: true,
            leftOffset: rect.left - clientX,
            topOffset: rect.top - clientY,
            left: rect.left,
            top: rect.top,
        });
    } 

    handleMouseMove = (e) => {
        if (!this.props.noDragging) {            
            //e.preventDefault();
            if (this.state.clicked) {
              this.drag(e.clientX, e.clientY);
              window.getSelection().removeAllRanges();
            }
        }
    }

    drag = (x, y) => {
        console.log('during drag')
        //this.generateEnterLeaveEvents(x, y);
        const stateChanges = { dragging: true };
        
        this.setState(stateChanges);
        //this.props.onDrag(this.props.dragData, this.currentTarget, x, y);
    };

    handleMouseUp = (e) => {
        this.setState({ clicked: false });
        if (this.state.dragging) {
            document.removeEventListener('mousemove', this.handleMouseMove);
            document.removeEventListener('mouseup', this.handleMouseUp);
            this.drop(e.clientX, e.clientY);
            window.getSelection().removeAllRanges();
        }
    }

    drop = (clientX, clientY) => {
        console.log('drop')
        this.containerElem.style.cursor = 'default';
        this.setState({ dragging: false });
    }

    render() {
        const content = this.props.children;

        return (
            <div ref={(c) => {this.containerElem = c;}}>
                {content}
            </div>
        );
    }
}

Draggable.defaultProps = {
    children: null,
    onDragStart: () => {},
    onDrag: () => {},
    onDragEnd: () => {},
    onDrop: () => {},
    noDragging: false,
    render: null,
  };

export default Draggable;
