import React, { Component } from 'react';
import styled from 'styled-components';
import Draggable from './Draggable';

const Content = styled.textarea`
    width: 200px;
    height: 200px;
    border: solid;
    border-width: 1px;
    box-shadow: 5px 5px 8px 4px rgba(0, 0, 0, 0.5);
    background-color: #BAECFF;
    font-size: 1.5em;
    resize: none;
    overflow: hidden;
    maxlength: 50;
`;

class PostIt extends Component {
    constructor(props) {
        super(props);

        this.state = {
            text: '',
        };
    }

    componentWillReceiveProps(nextProps) {
        this.props = nextProps;
    }

    shouldComponentUpdate() {
        let container = this.props.container;
        let boards = container.getElementsByTagName('TEXTAREA');
        

        console.log(container[0]);


        return true;
    }

    componentDidUpdate() {
        let boards  = document.getElementsByClassName('board');
        for(let board of boards) {
            let postIts = board.getElementsByTagName('TEXTAREA');
            for(let postIt of postIts) {
                postIt.removeAttribute('readOnly');
                postIt.addEventListener('change', this.OnChange);
            } 
        }
    }

    OnChange = (e) => {
        console.log(this.props.container)
    }
    
    render() {
        return (
            <Draggable tag="board" setContainer={this.props.setContainer} customEvent={this.OnChange}>   
                <form>
                    <Content readOnly onChange={this.OnChange} />
                </form>
            </Draggable>
        );
    }
}

export default PostIt;
