import React, { Component } from 'react';
import styled from 'styled-components';
import * as Utility from '../lib/Utility';

import tileImg from '../resources/tile.jpg';
import DropZone from './DropZone';

const Main = styled.div`
    position: relative;
    top: ${props => props.rect.top}px;
    left: ${props => props.rect.left}px;
    width: ${props => props.rect.width}px;
    height: ${props => props.rect.height}px;
    border: solid;
    border-width: 2px;
    box-shadow: 5px 5px 8px 4px rgba(0, 0, 0, 0.5);
`;

const Background = styled.div`
    float: left;
    line-height: 0px;
`;

class Board extends Component {
    constructor(props) {
        super(props);

        this.state= {
            background: null,
            left: 0,
            top: 0,
            width: 0,
            height: 0,
        }
    }

    componentWillMount() {        
        const offsetW = 50;
        const offsetH = 50;
        const row = this.props.row;
        const col = this.props.col;

        this.setState({
            background: null,
            //    <Tile offsetX={offsetW} offsetY={offsetH} row={row} col={col} key={0}/> 
            
            width: col * offsetW,
            height: row * offsetH,
        });
        
        this.GetObjects();
    }

    GetObjects = async () => {
        //const objects = await this.CallApi();
        //this.props.setContainer(objects);
    }

    CallApi = () => {
    }
    
    render() {
        let items = Utility.ConvertToHTML(this.props.items);

        console.log(tileImg);

        const rect  = {
            left: 50,
            top: 50,
            width: this.state.width,
            height: this.state.height
        };        

        return (  
            <DropZone tag="board" rect={rect}>
                <Main rect={rect}>
                    <Tiles />
                </Main>
                <div dangerouslySetInnerHTML={{ __html: items }} />
            </DropZone>
        );
    }
}

function Tiles(props) {
    let tiles = [];
    const size = props.row * props.col;
    for (let i = 0; i < size; ++i) {
        tiles.push(<img 
            src={tileImg} width={props.offsetX} height={props.offsetY} 
            key={i} alt="tile" draggable="false" />);
    }

    return (
        <Background>
            {tiles}
        </Background>
    );
}

export default Board;
