import React, { Component } from 'react';

function usesLeftButton(e) {
    const button = e.buttons || e.which || e.button;
    return button === 1;
}

class Draggable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            leftOffset: 0,
            topOffset: 0,
            left: 0,
            top: 0,
            clicked: false,
            dragging: false,
        }

        this.sourceElem = null;
        this.dragElem = null;
        this.currentTarget = null;
        this.prevTarget = null;
    }    

    componentDidMount() {
        const imgs = this.sourceElem.getElementsByTagName('IMG');
        for (let i = 0; i < imgs.length; ++i) {
          imgs[i].setAttribute('draggable', 'false');
        }

        this.addListeners(this.sourceElem);
    }

    addListeners = (element) => {
        element.addEventListener('mousedown', (e) => { this.handleMouseDown(e); });
    };

    setCurrentTarget = (x, y) => {
        const target = document.elementFromPoint(x, y) || document.body;
        this.dragElem.style.zIndex = this.props.zIndex;
        // prevent it from selecting itself as the target
        this.currentTarget = this.dragElem.contains(target) ? document.body : target;
    };

    handleMouseDown = (e) => {
        if(usesLeftButton(e))
        {
            console.log('down');            
            this.props.target.push(this.sourceElem.cloneNode(true));
            this.dragElem = this.sourceElem.cloneNode(true);
            document.addEventListener('mousemove', this.handleMouseMove);
            document.addEventListener('mouseup', this.handleMouseUp);
            this.startDrag(e.clientX, e.clientY);
        }
    }

    startDrag = (clientX, clientY) => {
       const rect = this.dragElem.getBoundingClientRect();
        this.setState({
            clicked: true,
            leftOffset: rect.left - clientX,
            topOffset: rect.top - clientY,
            left: rect.left,
            top: rect.top,
        });
    } 

    handleMouseMove = (e) => {
        e.preventDefault();
        if (this.state.clicked) {
            this.drag(e.clientX, e.clientY);
            window.getSelection().removeAllRanges();
        }
    }

    drag = (x, y) => {     
        this.setState({dragging: true});
    };

    handleMouseUp = (e) => {
        this.setState({ clicked: false });
        if (this.state.dragging) {
            document.removeEventListener('mousemove', this.handleMouseMove);
            document.removeEventListener('mouseup', this.handleMouseUp);
            this.drop(e.clientX, e.clientY);
            window.getSelection().removeAllRanges();
        }
    }

    drop = (clientX, clientY) => {
        this.setState({ dragging: false });
    }

    render() {
        const content = this.props.children;
        
        return (
            <div ref={(c) => {this.sourceElem = c;}}>
                {content}                    
            </div>
        );
    }
}

Draggable.defaultProps = {
    children: null,
    onDragStart: () => {},
    onDrag: () => {},
    onDragEnd: () => {},
    onDrop: () => {},
    render: null,
    zIndex: 1000,
  };

export default Draggable;
